    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed

        // TODO add your handling code here:
        // forma de guardar la info.
        String a=txtNombre.getText();
        String b=txtApellido.getText();
        String c=txtEdad.getText();
        String d=txtSignoZodiacal.getText();
        String e=txtBias.getText();
        String f=txtPrimeraCancion.getText();
        String g=txtAlias.getText();
        //pasamos a integer la edad
        int h=Integer.parseInt(c);
        Botones.guardar(a, b, h, d, e, f, g);
        
        //VAMOS A CREAR UN METODO PARA LIMPIAR
        txtNombre.setText("");
        txtApellido.setText("");
        txtEdad.setText("");
        txtSignoZodiacal.setText("");
        txtBias.setText("");
        txtPrimeraCancion.setText("");
        txtAlias.setText("");
        
    }//GEN-LAST:event_btnGuardarActionPerformed

