      public void cargaDatos(Usuarios per){
      this.txtNombre.setText(per.getNombre());
      this.txtApellido.setText(per.getApellidos());
      this.txtEdad.setText(Integer.toString(per.getEdad()));
      this.txtSignoZodiacal.setText(per.getSignoZodiacal());
      this.txtBias.setText(per.getBias());
      this.txtPrimeraCancion.setText(per.getPrimeraCancion());
      this.txtAlias.setText(per.getAlias());
      
      
      }
